mapper.insert:
INSERT INTO
user_template(

)
VALUES (#{id},#{cname},#{number},#{userId},#{templateId},#{mongoId},#{createTime},#{status},#{viewCount},#{appId});

    select
    LAST_INSERT_ID() as id

mapper.update:
UPDATE user_template

    cname=#{cname},
    mongoId=#{mongoId},
    viewCount=#{viewCount},
    status=#{status}

WHERE ID = #{id};

UPDATE user_template

    cname=#{cname},
    mongoId=#{mongoId},
    viewCount=#{viewCount},
    status=#{status}

WHERE userId=#{userId} and number=#{number};

mapper.select:
select user_template.*,`user`.`name` userName,template.`name` templateName,template.`url` templateUrl,category.name categoryName
from user_template
LEFT JOIN user ON user_template.userId=`user`.id
LEFT JOIN template ON user_template.templateId=template.id
LEFT JOIN category ON template.categoryId=category.id

     user_template.userId=#{userId}
    AND user_template.number=#{number}
    AND category.id=#{categoryId}
    AND user_template.cname like CONCAT('%','${cname}','%' ) 
    AND template.name like CONCAT('%','${categoryName}','%' ) 
    AND user_template.templateId=#{templateId}
    AND user_template.status=#{status} 
     AND user_template.createTime>=#{startTime}
     AND user_template.createTime<=#{endTime}
